import React, { Component } from 'react';
import './App.css'; 

// Componente principal: App
class App extends Component {
  render() {
    return (
      <div className="App">
        <h1>Ejercicios de Estado y Ciclo de Vida en React 03 Mon</h1>
        <Contador />
        <CicloDeVida />
        <ComponenteCombinado />
      </div>
    );
  }
}

export default App;

/*
Ejercicio 1: Contador simple
Crea un componente que muestre un contador. Al hacer clic 
en un botón, el contador debe incrementarse.
Objetivo: Que los estudiantes entiendan cómo funciona 
this.state y el método setState para actualizar el 
estado.
*/

// Ejercicio 1: Contador simple
class Contador extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contador: 0
    };
  }

  incrementarContador = () => {
    this.setState({ contador: this.state.contador + 1 });
  };

  render() {
    return (
      <div className="component">
        <h2>Contador Simple</h2>
        <div className="counter-display">
          <h3>{this.state.contador}</h3>
        </div>
        <button className="btn" onClick={this.incrementarContador}>
          Incrementar
        </button>
      </div>
    );
  }
}

// Ejercicio 2: Ciclo de vida de un componente
class CicloDeVida extends Component {
  constructor(props) {
    super(props);
    this.state = {
      valor: 0
    };
  }

  componentDidMount() {
    console.log('El componente se ha montado');
  }

  componentDidUpdate(prevProps, prevState) {
    console.log('El componente se ha actualizado');
  }

  componentWillUnmount() {
    console.log('El componente se desmontará');
  }

  incrementarValor = () => {
    this.setState({ valor: this.state.valor + 1 });
  };

  render() {
    return (
      <div className="component">
        <h2>Ciclo de Vida del Componente</h2>
        <div className="counter-display">
          <h3>{this.state.valor}</h3>
        </div>
        <button className="btn" onClick={this.incrementarValor}>
          Incrementar Valor
        </button>
      </div>
    );
  }
}

// Componente Combinado: Estado y Ciclo de Vida
class ComponenteCombinado extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contador: 0
    };
  }

  componentDidMount() {
    console.log('Componente combinado montado');
  }

  componentDidUpdate(prevProps, prevState) {
    console.log('Componente combinado actualizado');
  }

  componentWillUnmount() {
    console.log('Componente combinado desmontado');
  }

  incrementarContador = () => {
    this.setState({ contador: this.state.contador + 1 });
  };

  render() {
    return (
      <div className="component">
        <h2>Componente Combinado</h2>
        <div className="counter-display">
          <h3>{this.state.contador}</h3>
        </div>
        <button className="btn" onClick={this.incrementarContador}>
          Incrementar Combinado
        </button>
      </div>
    );
  }
}
