import React, { Component } from 'react';
import './App.css'; 

// Componente principal: App
class App extends Component {
  render() {
    return (
      <div className="App">
        <h1>Ejercicios de Estado y Ciclo de Vida en React 03 Mon</h1>
        <Contador />
        <CicloDeVida />
        <ComponenteCombinado />
      </div>
    );
  }
}

export default App;

// Ejercicio 1: Contador simple
class Contador extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contador: 0
    };
  }

  incrementarContador = () => {
    this.setState({ contador: this.state.contador + 1 });
  };

  render() {
    return (
      <div className="component">
        <h2>Contador Simple</h2>
        <div className="counter-display">
          <h3>{this.state.contador}</h3>
        </div>
        <button className="btn" onClick={this.incrementarContador}>
          Incrementar
        </button>
      </div>
    );
  }
}

// Ejercicio 2: Ciclo de vida de un componente
class CicloDeVida extends Component {
  constructor(props) {
    super(props);
    this.state = {
      valor: 0,
      montado: true
    };
  }

  componentDidMount() {
    console.log('2.1. El componente se ha MONTADO - Ejercicio #2');
  }

  componentDidUpdate(prevProps, prevState) {
    console.log('2.2. El componente se ha ACTUALIZADO - Ejercicio #2');
  }

  componentWillUnmount() {
    console.log('2.3. El componente se DESMONTARÁ - Ejercicio #2');
  }

  incrementarValor = () => {
    this.setState({ valor: this.state.valor + 1 });
  };

  desmontarComponente = () => {
    this.setState({ montado: false });
  };

  render() {
    if (!this.state.montado) return null; // No renderiza nada si no está montado

    return (
      <div className="component">
        <h2>Ciclo de Vida del Componente</h2>
        <div className="counter-display">
          <h3>{this.state.valor}</h3>
        </div>
        <button className="btn" onClick={this.incrementarValor}>
          Incrementar Valor
        </button>
        <button className="btn" onClick={this.desmontarComponente}>
          Desmontar Componente
        </button>
      </div>
    );
  }
}

// Componente Combinado
class ComponenteCombinado extends Component {
  constructor(props) {
    super(props);
    this.state = {
      contador: 0,
      montado: true
    };
  }

  componentDidMount() {
    console.log('3.1. El componente combinado se ha MONTADO - Ejercicio #3');
  }

  componentDidUpdate(prevProps, prevState) {
    if (prevState.montado !== this.state.montado) {
      console.log('3.2. El componente combinado ha cambiado de estado - Ejercicio #3');
    }
  }

  componentWillUnmount() {
    console.log('3.3. EL componente combinado se DESMONTARÁ - Ejercicio #3');
  }

  incrementarContador = () => {
    this.setState({ contador: this.state.contador + 1 });
  };

  desmontarComponente = () => {
    this.setState({ montado: false });
  };

  render() {
    if (!this.state.montado) return null; // No renderiza nada si no está montado

    return (
      <div className="component">
        <h2>Componente Combinado</h2>
        <div className="counter-display">
          <h3>{this.state.contador}</h3>
        </div>
        <button className="btn" onClick={this.incrementarContador}>
          Incrementar Combinado
        </button>
        <button className="btn" onClick={this.desmontarComponente}>
          Desmontar Componente Combinado
        </button>
      </div>
    );
  }
}
