import React, { Component } from 'react';

class CicloDeVida extends Component {
  constructor(props) {
    super(props);
    this.state = {
      valor: 0
    };
  }

  // componentDidMount: Se ejecuta después de que el componente se monta.
  componentDidMount() {
    console.log('El componente se ha montado');
  }

  // componentDidUpdate: Se ejecuta cuando se actualiza el componente.
  componentDidUpdate(prevProps, prevState) {
    console.log('El componente se ha actualizado');
  }

  // componentWillUnmount: Se ejecuta justo antes de desmontar el componente.
  componentWillUnmount() {
    console.log('El componente se desmontará');
  }

  incrementarValor = () => {
    this.setState({ valor: this.state.valor + 1 });
  };

  render() {
    return (
      <div>
        <h1>Valor: {this.state.valor}</h1>
        <button onClick={this.incrementarValor}>Incrementar</button>
      </div>
    );
  }
}

export default CicloDeVida;
