import React from 'react';
import Swal from 'sweetalert2';

const ButtonClick = () => {
  const handleClick = () => {
    // Utiliza SweetAlert2 en lugar de alert()
    Swal.fire({
      title: '¡Botón clickeado!',
      text: 'Has presionado el botón correctamente.',
      icon: 'success', // Puedes cambiar el icono (success, error, warning, info)
      confirmButtonText: 'Aceptar',
      customClass: {
        confirmButton: 'btn-confirm',
      },
      buttonsStyling: false, // Deshabilita los estilos de botón predeterminados para usar CSS personalizado
    });
  };

  return (
    <div id="button-click-container">
      <button className="btn" onClick={handleClick}>Click Me</button>
    </div>
  );
};

export default ButtonClick;
