import React, { useState } from 'react';
import Swal from 'sweetalert2'; // Importa la biblioteca SweetAlert2

const FormularioUsuario = () => {
  const [nombre, setNombre] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Reemplaza alert() por SweetAlert2
    Swal.fire({
      title: '¡Hola!',
      text: `¡Hola, ${nombre}!`,
      icon: 'success',
      confirmButtonText: 'Aceptar',
      customClass: {
        confirmButton: 'btn-confirm', // Usamos nuestra clase personalizada para los botones
      },
      buttonsStyling: false, // Deshabilita los estilos de botón predeterminados
    });
  };

  return (
    <div className="card">
      <h2>Formulario de Usuario</h2>
      <form onSubmit={handleSubmit} className="formulario">
        <label htmlFor="nombre" className="label">Nombre: </label>
        <input
          type="text"
          id="nombre"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          className="input-field"
        />
        <button type="submit" className="btn">Enviar</button>
      </form>
      <p className="result">Tu nombre es: {nombre}</p>
    </div>
  );
};

export default FormularioUsuario;
