/*
Tarea 4: Explora la documentación de Tailwind CSS y utiliza 
esta librería para crear un componente Header con un 
menú de navegación estilizado. Utiliza clases de Tailwind 
para darle estilo al fondo, el espaciado, y el tamaño del 
texto. 
*/

import React from 'react';

const Header = () => {
  return (
    <header className="bg-[#2980b9] bg-gradient-to-r from-[#2980b9] to-[#2c3e50] p-6 shadow-lg">
      <div className="container mx-auto">
        <nav>
          <ul className="flex justify-center flex-wrap list-none p-0 m-0">
            {['Inicio', 'Acerca de', 'Servicios', 'Contacto'].map((item) => (
              <li key={item} className="mx-4 my-2">
                <a
                  href="#"
                  className="text-white text-lg font-semibold relative transition-all duration-300 hover:text-yellow-300"
                >
                  {item}
                  <span className="block h-0.5 bg-yellow-300 transition-all duration-300 absolute left-0 bottom-0 w-0 group-hover:w-full"></span>
                </a>
              </li>
            ))}
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
